import styled from "styled-components";

export const CardWrapper = styled.div`
  padding: 16px;
  display: flex;
  flex-direction: column;
  align-items: center;
  height: 300px;
  width: 200px;
  border-radius: 16px;
  background-color: #d9e3f0;
  margin: 0px 20px 10px 0px;
`;

export const ImgWrapper = styled.div`
  width: 100px;
  height: 100px;
  border-radius: 50%;
  background-color: beige;
`
