import React, { useEffect, useState } from "react";
import marvelApi, { marvelApiKey } from "../../services/marvel-api";
import Card from "../card-hero";
import { Wrapper } from "./styles";



export default function Container(props:React.PropsWithChildren){
  const [heroes, setHeroes] = useState([]);
  const [offset, setOffset] = useState(0)

  useEffect(() => {
    marvelApi.get(`?limit=100&offset=${offset}&apikey=${marvelApiKey}`)
       .then((response) => {
         console.log(response);
         setHeroes(response.data)
      })
      .catch((err) => {
        console.error("ops! ocorreu um erro : " + err);
      });
  }, []);
  console.log(heroes)
  return (
    <Wrapper>
      <Card/>
    </Wrapper>
    );
}
