import React, { useState } from "react";
import api from "../../services/api";
import { Button } from "./styles";

type HeroTYpe = {
  heroName: string;
  description: string;
  img: string;
}
interface IButtonProps{
  hero: HeroTYpe;
}
export default function CardButton({hero, ...props}:IButtonProps){
  const [selected, setSelected] = useState(false);
  const [heroId, setHeroId] = useState(0);

  const insertHero = (hero: string) => {
    api.post("/heroi", hero);
  }

  //const deleteHero = (heroid) => {
  //  api.delete("/heroi", heroid);
  //}

  const handleClick = () => {
    insertHero(JSON.stringify(hero));
    setSelected(!selected);
  }
  return(
      <Button onClick={() => handleClick()} style={{backgroundColor: selected ? '#4caf50' : '#abb8c3'}}>{selected? 'Add' : 'Remove'} Hero</Button>
  );
}
