import axios from "axios";
import React, { useEffect, useState } from "react";
import api from "../../services/api";
import Card from "../card-hero";
import { Wrapper } from "./styles";

const apiKey = "apiKey dd42e0b583b5dd05c53a12954ba7ae1f"

export default function Container(props:React.PropsWithChildren){
  const [heroes, setHeroes] = useState([] as apiService.Response);

  useEffect(() => {
    axios.get("https://gateway.marvel.com/v1/public/characters?limit=5&offset=0", {headers:{'Authorization': apiKey, 'Access-Control-Allow-Origin': '*', 'Content-Type': 'application/json', }})
       .then((response) => {
         console.log(response.data);
         setHeroes(response.data)
      })
      .catch((err) => {
        console.error("ops! ocorreu um erro : " + err);
      });
  }, []);
  console.log(heroes)
  return (
    <Wrapper>
      {heroes.map((item) => 
        <Card 
          heroName={item.name} 
          heroDescription={item.description} 
          heroImg={item.img}/>
        )}

        </Wrapper>
    );
}
