import styled from "styled-components";

export const Button = styled.button`
  width: 50%;
  height: 100px;
  display: flex;
  justify-content: center;
  align-items: center;
  border-radius: 20px;
  background-color:  #DBBB4B;
  border: none;
`;
export const TitleButton = styled.div`
  font-family: system-ui;
  font-size: 24px;
  font-weight: 400;
  color: #00004f ;
`;
