import React, { useState } from "react";
import { Button } from "./styles";

interface IButtonProps{
  onPress?: () => void;
}
export default function CardButton({onPress, ...props}:IButtonProps){
  const [selected, setSelected] = useState(false);
  return(
      <Button onClick={onPress} style={{backgroundColor: selected ? '#4caf50' : '#abb8c3'}}>Add Hero</Button>
  );
}
