  }
  const deleteHero = (id:number) => {
    console.log('requisicao');
  }
  return (
    <Wrapper>
      <TeamName>{team.name}</TeamName>
      <AvaliablesWrapper>
        {getSelectedHeroes().map((item) => <TeamHero key={`no-time-hero-${item.id}`} hero={item} onClick={() => settTeamHeroes(item.id)} />)}
      </AvaliablesWrapper>
      <SelectedWrapper>
      {teamHeroes.map((item) => <TeamHero key={`time-hero-${item.id}`} hero={item} onClick={() => deleteHero(item.id)} />)}x
      </SelectedWrapper>
    </Wrapper>
  );
}
