import React, { useState } from "react";
import TeamHero from "../team-hero";
import { AvaliablesWrapper, SelectedWrapper, TeamName, Wrapper } from "./styles";

interface ITeamProps {
  team: {id:number, name: string, heroes:apiService.Character[]};
}
export default function TeamCard({team}:ITeamProps){
  const [chooseHero, setChooseHero] = useState(false);
  const getSelectedHeroes = () =>{
    console.log('requisicao');
    return [{id:1, nome:'exemplo hero', descricao: 'ex', imagem: 'https://www.zooplus.pt/magazine/wp-content/uploads/2021/03/kitten-sitzt-boden-768x512-1.jpeg'}];
  }
  const getTeamHeroes = () => {
    console.log('requisicao');
    setChooseHero(false);
    return [{id:1, nome:'exemplo hero', descricao: 'ex', imagem: 'https://www.zooplus.pt/magazine/wp-content/uploads/2021/03/kitten-sitzt-boden-768x512-1.jpeg'}];
  }
  const deleteHero = (id:number) => {
    setChooseHero(true);
    console.log('requisicao');
    setChooseHero(false);
  }
  return (
    <Wrapper>
      <TeamName>{team.name}</TeamName>
      <AvaliablesWrapper>
        {getSelectedHeroes().map((item) => <TeamHero hero={item} onClick={() => setChooseHero(true)} />)}
      </AvaliablesWrapper>
      <SelectedWrapper>
        {chooseHero && getTeamHeroes().map((item) => <TeamHero hero={item} onClick={deleteHero(item.id)} />)}
      </SelectedWrapper>
    </Wrapper>
  );
}
